package tr.edu.medipol.yazilim.meryem.cikarma;

public class Cikarma {
	
	static int cikar(int sayi1, int sayi2) {
		return sayi1 - sayi2;
	}
	


	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		int sonuc = cikar(250,100);
		
		System.out.println("250 + 100 = " + sonuc);

	}

}
